mapper.update:
UPDATE member

	pw = #{pw},
	nickname = #{nickname},
	address = #{address},
	phone = #{phone}

WHERE id = #{id}

UPDATE profile
SET
image = #{image}
WHERE id = (SELECT id FROM member WHERE id = #{id});

UPDATE member
SET
profile_use = 'Y' WHERE id = #{id}

UPDATE
profile
SET
profile_use = #{profileVisibility}
WHERE id = #{id}

UPDATE profile
SET
exercise_use = #{mateSearch}
WHERE id = #{id}

mapper.insert:
INSERT INTO mate
(id, unlike_id, mate_date)
VALUES (#{id}, #{unlikeId}, NOW());

mapper.delete:
DELETE FROM
mate_application
WHERE id = #{id} AND unlike_id = #{unlikeId};

DELETE FROM
mate_application
WHERE id = #{id} AND unlike_id = #{unlikeId};

DELETE FROM
mate_application
WHERE id = #{unlikeId} AND unlike_id = #{id};

mapper.update:
UPDATE member
SET
icon_image = (
SELECT image
FROM icon
WHERE icon.icon_idx = #{iconId}
)
WHERE id = #{id};

mapper.select:
SELECT m.id, m.unlike_id, m.mate_idx, m.mate_date, p.image AS friend_image FROM mate m INNER JOIN profile p ON p.id = m.id WHERE m.unlike_id = #{id}
UNION
SELECT m.id, m.unlike_id, m.mate_idx, m.mate_date, p.image AS friend_image FROM mate m INNER JOIN profile p ON p.id = m.unlike_id WHERE m.id = #{id} ORDER BY mate_date DESC LIMIT #{limit} OFFSET #{offset}
